import React, { useState, useEffect } from "react";
import { movieDetailGetFetch } from "./apis/movieDetailGetFetch";

const Detail = ({ movieId }) => {
  const [loading, setLoading] = useState(true);
  const [showDetails, setShowDetails] = useState(false);
  const [info, setInfo] = useState({});

  useEffect(() => {
    const fetchMovieDetail = async () => {
      try {
        const {
          data: {
            movieInfoResult: { movieInfo },
          },
        } = await movieDetailGetFetch(movieId);

        setInfo({ ...movieInfo });
        setLoading(false);
      } catch (error) {
        console.error(error);
      }
    };

    fetchMovieDetail();
  }, [movieId]);

  const {
    actors,
    audits,
    directors,
    genres,
    movieNm,
    movieNmEn,
    prdtStatNm,
    prdtYear,
    typeNm,
  } = info;

  const handleShowDetails = () => {
    setShowDetails(true);
  };

  return loading ? (
    <div>로딩중입니다..</div>
  ) : (
    <div>
      <div>
        <h1>
          {movieNm} ({movieNmEn})
        </h1>
        <div>
          <div>감독</div>
          <div>
            {directors?.map((director, i) => (
              <span key={i}>{director.peopleNm}<br /></span>
            ))}
          </div>
        </div>
        <div>
          {audits?.map((rule) => rule.watchGradeNm)}
        </div>
        <div>
          {prdtYear}년 {prdtStatNm}
        </div>
      </div>

      {showDetails && (
        <div>
          <div>
            <h3>장르</h3>
            {genres?.map((genre, i) => (
              <div key={i}>{genre.genreNm}</div>
            ))}
          </div>

          <div>
            <h3>배우들</h3>
            <ul style={{ listStyle: "none" }}>
              {actors?.map((actor, i) => (
                <li key={i}>
                  <div>
                    이름: {actor.peopleNm} 배역: {actor.cast}
                  </div>
                </li>
              ))}
            </ul>
          </div>

          <div>
            <h3>영화 유형</h3>
            <div>{typeNm}</div>
          </div>
        </div>
      )}

      {!showDetails && (
        <button onClick={handleShowDetails}>상세정보 보기</button>
      )}
    </div>
  );
};

export default Detail;
